import React from "react"

export function useDefault<T, D>(
  initialValue: T,
  defaultValue: D
): [D | Exclude<T, null | undefined>, React.Dispatch<React.SetStateAction<T>>] {
  const [state, setState] = React.useState<T>(initialValue)

  if (state === null || state === undefined) {
    return [defaultValue, setState]
  }

  return [state as D | Exclude<T, null | undefined>, setState]
}
